import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { Link } from "react-router-dom";

const destinations = [
  "Jaipur",
  "Goa",
  "Custom Destination",
  "India1",
  "Ooty",
  "Kolkata",
  "Ladakh",
  "Munnar",
];

function Bookings() {
  const navigate = useNavigate();
  const { user } = useAuth();

  const [form, setForm] = useState({
    id: null,
    name: "",
    email: "",
    phone: "",
    destination: destinations[0],
    startDate: "",
    endDate: "",
    travelers: 1,
  });

  const [errors, setErrors] = useState({});
  const [bookings, setBookings] = useState([]);
  const [filter, setFilter] = useState({ destination: "", date: "" });
  const [notification, setNotification] = useState("");

  useEffect(() => {
    if (user) {
      const saved =
        JSON.parse(localStorage.getItem(`bookings_${user.username}`)) || [];
      setBookings(saved);
    } else {
      setBookings([]);
    }
  }, [user]);

  useEffect(() => {
    if (user) {
      localStorage.setItem(
        `bookings_${user.username}`,
        JSON.stringify(bookings)
      );
    }
  }, [bookings, user]);

  const validate = () => {
    let tempErrors = {};
    if (!form.name) tempErrors.name = "Name is required";
    if (!form.email) tempErrors.email = "Email is required";
    else if (!/\S+@\S+\.\S+/.test(form.email))
      tempErrors.email = "Email is invalid";
    if (!form.phone) tempErrors.phone = "Phone is required";
    if (!form.startDate) tempErrors.startDate = "Start date is required";
    if (!form.endDate) tempErrors.endDate = "End date is required";
    if (form.travelers < 1)
      tempErrors.travelers = "Must be at least 1 traveler";
    return tempErrors;
  };

  const resetForm = () => {
    setForm({
      id: null,
      name: "",
      email: "",
      phone: "",
      destination: destinations[0],
      startDate: "",
      endDate: "",
      travelers: 1,
    });
    setErrors({});
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const tempErrors = validate();
    setErrors(tempErrors);

    if (Object.keys(tempErrors).length === 0) {
      if (form.id) {
        setBookings((prev) => prev.map((b) => (b.id === form.id ? form : b)));
        setNotification("Booking updated successfully!");
      } else {
        const newBooking = { ...form, id: Date.now() };
        setBookings((prev) => [...prev, newBooking]);
        setNotification("Booking confirmed successfully!");
        setTimeout(() => {
          navigate("/confirmation");
        }, 1500);
      }
      resetForm();
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const handleEdit = (booking) => {
    setForm(booking);
  };

  const handleCancel = (id) => {
    if (window.confirm("Are you sure you want to cancel this booking?")) {
      setBookings((prev) => prev.filter((b) => b.id !== id));
      setNotification("Booking cancelled.");
      setTimeout(() => setNotification(""), 3000);
      if (form.id === id) resetForm();
    }
  };

  const filteredBookings = bookings.filter((b) => {
    const matchesDestination = filter.destination
      ? b.destination.toLowerCase().includes(filter.destination.toLowerCase())
      : true;
    const matchesDate = filter.date ? b.startDate === filter.date : true;
    return matchesDestination && matchesDate;
  });

  if (!user) {
    return (
      <div className="p-10 text-center text-red-600">
        Please login to book and view your bookings.
      </div>
    );
  }

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow-lg mt-8">
      <h2 className="text-3xl font-bold mb-6 text-center text-indigo-700">
        Book Your Trip
      </h2>

      {notification && (
        <div className="fixed top-5 right-5 bg-green-500 text-white p-3 rounded shadow-lg z-50">
          {notification}
        </div>
      )}

      <form onSubmit={handleSubmit} noValidate>
        <label className="block mb-2 font-semibold">Name:</label>
        <input
          type="text"
          name="name"
          value={form.name}
          onChange={handleChange}
          className={`w-full p-2 border rounded ${
            errors.name ? "border-red-500" : "border-gray-300"
          }`}
        />
        {errors.name && (
          <p className="text-red-600 text-sm mb-3">{errors.name}</p>
        )}

        <label className="block mb-2 font-semibold">Email:</label>
        <input
          type="email"
          name="email"
          value={form.email}
          onChange={handleChange}
          className={`w-full p-2 border rounded ${
            errors.email ? "border-red-500" : "border-gray-300"
          }`}
        />
        {errors.email && (
          <p className="text-red-600 text-sm mb-3">{errors.email}</p>
        )}

        <label className="block mb-2 font-semibold">Phone:</label>
        <input
          type="tel"
          name="phone"
          value={form.phone}
          onChange={handleChange}
          className={`w-full p-2 border rounded ${
            errors.phone ? "border-red-500" : "border-gray-300"
          }`}
        />
        {errors.phone && (
          <p className="text-red-600 text-sm mb-3">{errors.phone}</p>
        )}

        <label className="block mb-2 font-semibold">Destination:</label>
        <select
          name="destination"
          value={form.destination}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded mb-4"
        >
          {destinations.map((dest) => (
            <option key={dest} value={dest}>
              {dest}
            </option>
          ))}
        </select>

        <label className="block mb-2 font-semibold">Start Date:</label>
        <input
          type="date"
          name="startDate"
          value={form.startDate}
          onChange={handleChange}
          className={`w-full p-2 border rounded ${
            errors.startDate ? "border-red-500" : "border-gray-300"
          }`}
        />
        {errors.startDate && (
          <p className="text-red-600 text-sm mb-3">{errors.startDate}</p>
        )}

        <label className="block mb-2 font-semibold">End Date:</label>
        <input
          type="date"
          name="endDate"
          value={form.endDate}
          onChange={handleChange}
          className={`w-full p-2 border rounded ${
            errors.endDate ? "border-red-500" : "border-gray-300"
          }`}
        />
        {errors.endDate && (
          <p className="text-red-600 text-sm mb-3">{errors.endDate}</p>
        )}

        <label className="block mb-2 font-semibold">Number of Travelers:</label>
        <input
          type="number"
          name="travelers"
          min="1"
          value={form.travelers}
          onChange={handleChange}
          className={`w-full p-2 border rounded ${
            errors.travelers ? "border-red-500" : "border-gray-300"
          }`}
        />
        {errors.travelers && (
          <p className="text-red-600 text-sm mb-3">{errors.travelers}</p>
        )}

        <button
          type="submit"
          className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 rounded mt-4 transition duration-300"
        >
          {form.id ? "Update Booking" : "Book Now"}
        </button>
      </form>

      <div className="mt-10">
        <h3 className="text-2xl font-semibold mb-4 text-indigo-700">
          Your Booking History
        </h3>

        <div className="flex gap-4 mb-4">
          <input
            type="text"
            placeholder="Filter by destination"
            value={filter.destination}
            onChange={(e) =>
              setFilter({ ...filter, destination: e.target.value })
            }
            className="flex-1 p-2 border rounded border-gray-300"
          />
          <input
            type="date"
            value={filter.date}
            onChange={(e) => setFilter({ ...filter, date: e.target.value })}
            className="p-2 border rounded border-gray-300"
          />
          <button
            onClick={() => setFilter({ destination: "", date: "" })}
            className="bg-red-500 text-white px-4 rounded hover:bg-red-600"
          >
            Clear
          </button>
        </div>

        {filteredBookings.length === 0 ? (
          <p className="text-gray-600">No bookings found.</p>
        ) : (
          <ul className="space-y-4">
            {filteredBookings.map((b) => (
              <li
                key={b.id}
                className="border p-4 rounded shadow flex flex-col gap-1"
              >
                <div>
                  <strong>Name:</strong> {b.name}
                </div>
                <div>
                  <strong>Email:</strong> {b.email}
                </div>
                <div>
                  <strong>Phone:</strong> {b.phone}
                </div>
                <div>
                  <strong>Destination:</strong> {b.destination}
                </div>
                <div>
                  <strong>Dates:</strong> {b.startDate} to {b.endDate}
                </div>
                <div>
                  <strong>Travelers:</strong> {b.travelers}
                </div>

                <div className="mt-2 flex gap-3">
                  <button
                    onClick={() => handleEdit(b)}
                    className="bg-yellow-400 hover:bg-yellow-500 px-3 py-1 rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleCancel(b.id)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
                  >
                    Cancel
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Bookings;
