import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function BookingDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();

  // Early check if user is not logged in
  if (!user) {
    return (
      <div className="p-6 text-center text-red-600">
        Please login to view booking details.
        <br />
        <button
          onClick={() => navigate("/login")}
          className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded"
        >
          Go to Login
        </button>
      </div>
    );
  }

  // Try to get bookings from localStorage
  const bookingsJSON = localStorage.getItem(`bookings_${user.username}`);
  const bookings = bookingsJSON ? JSON.parse(bookingsJSON) : [];

  // Make sure id types match - convert both to strings
  const booking = bookings.find((b) => String(b.id) === String(id));

  if (!booking) {
    return (
      <div className="p-6 text-center text-red-600">
        Booking not found.
        <br />
        <button
          onClick={() => navigate(-1)}
          className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded"
        >
          Go Back
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow mt-8">
      <h2 className="text-3xl font-bold mb-6 text-indigo-700">
        Booking Details
      </h2>
      <p>
        <strong>Name:</strong> {booking.name}
      </p>
      <p>
        <strong>Email:</strong> {booking.email}
      </p>
      <p>
        <strong>Phone:</strong> {booking.phone}
      </p>
      <p>
        <strong>Destination:</strong> {booking.destination}
      </p>
      <p>
        <strong>Start Date:</strong> {booking.startDate}
      </p>
      <p>
        <strong>End Date:</strong> {booking.endDate}
      </p>
      <p>
        <strong>Travelers:</strong> {booking.travelers}
      </p>

      <button
        onClick={() => navigate(-1)}
        className="mt-6 px-4 py-2 bg-indigo-600 text-white rounded"
      >
        Back to Bookings
      </button>
    </div>
  );
}

export default BookingDetails;
