import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const destinations = [
  "Jaipur",
  "Goa",
  "Custom Destination",
  "India1",
  "Ooty",
  "Kolkata",
  "Ladakh",
  "Munnar",
];

function Bookings() {
  const navigate = useNavigate();
  const { user } = useAuth();

  const [form, setForm] = useState({
    id: null,
    name: "",
    email: "",
    phone: "",
    destination: destinations[0],
    startDate: "",
    endDate: "",
    travelers: 1,
  });

  const [errors, setErrors] = useState({});
  const [bookings, setBookings] = useState([]);
  const [filter, setFilter] = useState({ destination: "", date: "" });
  const [notification, setNotification] = useState("");
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    if (user) {
      const saved =
        JSON.parse(localStorage.getItem(`bookings_${user.username}`)) || [];
      setBookings(saved);
    } else {
      setBookings([]);
    }
  }, [user]);

  useEffect(() => {
    if (user) {
      localStorage.setItem(
        `bookings_${user.username}`,
        JSON.stringify(bookings)
      );
    }
  }, [bookings, user]);

  const validate = () => {
    let tempErrors = {};
    if (!form.name) tempErrors.name = "Name is required";
    if (!form.email) tempErrors.email = "Email is required";
    else if (!/\S+@\S+\.\S+/.test(form.email))
      tempErrors.email = "Email is invalid";
    if (!form.phone) tempErrors.phone = "Phone is required";
    if (!form.startDate) tempErrors.startDate = "Start date is required";
    if (!form.endDate) tempErrors.endDate = "End date is required";
    if (form.travelers < 1)
      tempErrors.travelers = "Must be at least 1 traveler";
    return tempErrors;
  };

  const resetForm = () => {
    setForm({
      id: null,
      name: "",
      email: "",
      phone: "",
      destination: destinations[0],
      startDate: "",
      endDate: "",
      travelers: 1,
    });
    setErrors({});
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const tempErrors = validate();
    setErrors(tempErrors);

    if (Object.keys(tempErrors).length === 0) {
      if (form.id) {
        setBookings((prev) => prev.map((b) => (b.id === form.id ? form : b)));
        setNotification("Booking updated successfully!");
      } else {
        const newBooking = { ...form, id: Date.now() };
        setBookings((prev) => [...prev, newBooking]);
        setNotification("Booking confirmed successfully!");
        setTimeout(() => {
          navigate("/confirmation");
        }, 1500);
      }
      resetForm();
      setShowForm(false);
      setTimeout(() => setNotification(""), 3000);
    }
  };

  const handleEdit = (booking) => {
    setForm(booking);
    setShowForm(true);
  };

  const handleCancel = (id) => {
    if (window.confirm("Are you sure you want to cancel this booking?")) {
      setBookings((prev) => prev.filter((b) => b.id !== id));
      setNotification("Booking cancelled.");
      setTimeout(() => setNotification(""), 3000);
      if (form.id === id) {
        resetForm();
        setShowForm(false);
      }
    }
  };

  const filteredBookings = bookings.filter((b) => {
    const matchesDestination = filter.destination
      ? b.destination.toLowerCase().includes(filter.destination.toLowerCase())
      : true;
    const matchesDate = filter.date ? b.startDate === filter.date : true;
    return matchesDestination && matchesDate;
  });

  if (!user) {
    return (
      <div className="p-10 text-center text-red-600">
        Please login to book and view your bookings.
      </div>
    );
  }

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow-lg mt-8">
      <h2 className="text-3xl font-bold mb-6 text-center text-indigo-700">
        Book Your Trip
      </h2>

      {notification && (
        <div className="fixed top-5 right-5 bg-green-500 text-white p-3 rounded shadow-lg z-50">
          {notification}
        </div>
      )}

      {!showForm && (
        <div className="mb-6 text-center">
          <button
            onClick={() => setShowForm(true)}
            className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-6 rounded"
          >
            New Booking
          </button>
        </div>
      )}

      {showForm && (
        <form onSubmit={handleSubmit} noValidate>
          <label className="block mb-2 font-semibold">Name:</label>
          <input
            type="text"
            name="name"
            value={form.name}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${
              errors.name ? "border-red-500" : "border-gray-300"
            }`}
          />
          {errors.name && (
            <p className="text-red-600 text-sm mb-3">{errors.name}</p>
          )}

          <label className="block mb-2 font-semibold">Email:</label>
          <input
            type="email"
            name="email"
            value={form.email}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${
              errors.email ? "border-red-500" : "border-gray-300"
            }`}
          />
          {errors.email && (
            <p className="text-red-600 text-sm mb-3">{errors.email}</p>
          )}

          <label className="block mb-2 font-semibold">Phone:</label>
          <input
            type="tel"
            name="phone"
            value={form.phone}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${
              errors.phone ? "border-red-500" : "border-gray-300"
            }`}
          />
          {errors.phone && (
            <p className="text-red-600 text-sm mb-3">{errors.phone}</p>
          )}

          <label className="block mb-2 font-semibold">Destination:</label>
          <select
            name="destination"
            value={form.destination}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded mb-4"
          >
            {destinations.map((dest) => (
              <option key={dest} value={dest}>
                {dest}
              </option>
            ))}
          </select>

          <label className="block mb-2 font-semibold">Start Date:</label>
          <input
            type="date"
            name="startDate"
            value={form.startDate}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${
              errors.startDate ? "border-red-500" : "border-gray-300"
            }`}
          />
          {errors.startDate && (
            <p className="text-red-600 text-sm mb-3">{errors.startDate}</p>
          )}

          <label className="block mb-2 font-semibold">End Date:</label>
          <input
            type="date"
            name="endDate"
            value={form.endDate}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${
              errors.endDate ? "border-red-500" : "border-gray-300"
            }`}
          />
          {errors.endDate && (
            <p className="text-red-600 text-sm mb-3">{errors.endDate}</p>
          )}

          <label className="block mb-2 font-semibold">
            Number of Travelers:
          </label>
          <input
            type="number"
            name="travelers"
            min="1"
            value={form.travelers}
            onChange={handleChange}
            className={`w-full p-2 border rounded ${
              errors.travelers ? "border-red-500" : "border-gray-300"
            }`}
          />
          {errors.travelers && (
            <p className="text-red-600 text-sm mb-3">{errors.travelers}</p>
          )}

          <div className="flex gap-4 mt-4">
            <button
              type="submit"
              className="flex-1 bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 rounded transition duration-300"
            >
              {form.id ? "Update Booking" : "Confirm Booking"}
            </button>
            <button
              type="button"
              onClick={() => {
                resetForm();
                setShowForm(false);
              }}
              className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-bold py-3 rounded transition duration-300"
            >
              Cancel
            </button>
          </div>
        </form>
      )}

      <div className="mt-10">
        <h3 className="text-2xl font-semibold mb-4 text-indigo-700">
          Booking History
        </h3>

        <div className="mb-4 flex gap-4">
          <input
            type="text"
            placeholder="Filter by destination"
            value={filter.destination}
            onChange={(e) =>
              setFilter((prev) => ({ ...prev, destination: e.target.value }))
            }
            className="flex-1 p-2 border border-gray-300 rounded"
          />
          <input
            type="date"
            value={filter.date}
            onChange={(e) =>
              setFilter((prev) => ({ ...prev, date: e.target.value }))
            }
            className="p-2 border border-gray-300 rounded"
          />
          <button
            onClick={() => setFilter({ destination: "", date: "" })}
            className="bg-red-500 hover:bg-red-600 text-white px-4 rounded"
          >
            Clear
          </button>
        </div>

        {filteredBookings.length === 0 ? (
          <p className="text-center text-gray-500">No bookings found.</p>
        ) : (
          filteredBookings.map((booking) => (
            <div
              key={booking.id}
              className="border border-gray-300 rounded p-4 mb-4 flex flex-col gap-2"
            >
              <Link
                to={`/booking/${booking.id}`}
                className="text-indigo-600 font-semibold hover:underline"
              >
                {booking.destination} - {booking.startDate} to {booking.endDate}
              </Link>
              <p>
                <strong>Name:</strong> {booking.name}
              </p>
              <p>
                <strong>Email:</strong> {booking.email}
              </p>
              <p>
                <strong>Phone:</strong> {booking.phone}
              </p>
              <p>
                <strong>Travelers:</strong> {booking.travelers}
              </p>
              <div className="flex gap-2 mt-2">
                <button
                  onClick={() => handleEdit(booking)}
                  className="bg-yellow-400 hover:bg-yellow-500 text-white py-1 px-3 rounded"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleCancel(booking.id)}
                  className="bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded"
                >
                  Cancel
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Bookings;
