import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function BookingDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [booking, setBooking] = useState(null);

  useEffect(() => {
    if (user) {
      const stored =
        JSON.parse(localStorage.getItem(`bookings_${user.username}`)) || [];
      const found = stored.find((b) => b.id.toString() === id);
      if (found) setBooking(found);
      else navigate("/bookings"); // Redirect if booking not found
    } else {
      navigate("/login"); // Redirect if not logged in
    }
  }, [id, user, navigate]);

  if (!booking) {
    return <div className="p-10 text-center">Loading booking details...</div>;
  }

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow-lg mt-10">
      <h2 className="text-3xl font-bold text-indigo-700 mb-6 text-center">
        Booking Details
      </h2>
      <div className="space-y-3 text-lg">
        <p>
          <strong>Name:</strong> {booking.name}
        </p>
        <p>
          <strong>Email:</strong> {booking.email}
        </p>
        <p>
          <strong>Phone:</strong> {booking.phone}
        </p>
        <p>
          <strong>Destination:</strong> {booking.destination}
        </p>
        <p>
          <strong>Start Date:</strong> {booking.startDate}
        </p>
        <p>
          <strong>End Date:</strong> {booking.endDate}
        </p>
        <p>
          <strong>Travelers:</strong> {booking.travelers}
        </p>
      </div>
      <button
        onClick={() => navigate("/bookings")}
        className="mt-6 bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
      >
        Back to Bookings
      </button>
    </div>
  );
}

export default BookingDetails;
