// src/pages/BookingDetails.js
import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const BookingDetails = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();

  if (!user) {
    return (
      <p className="p-10 text-center text-red-600">
        Please login to view booking details.
      </p>
    );
  }

  const bookings =
    JSON.parse(localStorage.getItem(`bookings_${user.username}`)) || [];
  const booking = bookings.find((b) => b.id === Number(id));

  if (!booking) {
    return <p className="p-10 text-center">Booking not found.</p>;
  }

  return (
    <div className="p-10 max-w-md mx-auto bg-white rounded shadow">
      <h2 className="text-3xl font-bold mb-6">Booking Details</h2>
      <p>
        <strong>Name:</strong> {booking.name}
      </p>
      <p>
        <strong>Email:</strong> {booking.email}
      </p>
      <p>
        <strong>Destination:</strong> {booking.destination}
      </p>
      <p>
        <strong>Date:</strong> {booking.date}
      </p>
      <button
        onClick={() => navigate(-1)}
        className="mt-6 bg-blue-600 text-white p-3 rounded hover:bg-blue-700"
      >
        Back
      </button>
    </div>
  );
};

export default BookingDetails;
